let Protein=ZatGizi(titleGizi: "Protein", definisiGizi: "Pada ibu hamil, Protein berguna sebagai komponen penyusun sel vital terutama untuk menciptakan kulit, otot, rambut, dan tulang. Protein Sangat dibutuhkan untuk memastikan pertumbuhan bayi normal dan mencegah berat lahir rendah serta menjaga kesehatan jaringan tubuh si Ibu.")
let VitaminA=ZatGizi(titleGizi: "Vitamin A", definisiGizi: "Pada ibu hamil, Vitamin A berguna untuk proses perkembangan jantung, paru-paru, ginjal, mata, dan Tulang janin. Setelah melahirkan, vitamin A membantu memperbaiki jaringan tubuh ibu, menurunkan resiko terjadinya infeksi, memperkuat daya tahan tubuh, dan menjaga kesehatan penglihatan.")
let VitaminB1=ZatGizi(titleGizi: "Vitamin B1", definisiGizi: "Pada ibu hamil, Vitamin B1 atau Thiamin berguna untuk perkembangan otak, sistem saraf, otot, dan jantung janin serta mencegah penyakit beri-beri pada Ibu. Selain itu, vitamin ini juga dapat mengubah karbohidrat menjadi energi, sehingga tubuh tidak terasa lemas saat hamil.")
 
let VitaminB2=ZatGizi(titleGizi: "Vitamin B2", definisiGizi: "Pada ibu hamil, Vitamin B2 atau Riboflavin berguna untuk mencerahkan kulit Ibu saat masa kehamilan serta konsumsi vitamin ini setiap hari dapat mencegah resiko pre-eklampsia sejak dini. Vitamin B2 Tidak disimpan oleh tubuh sehingga harus rajin dikonsumsi setiap harinya.")
 
let VitaminB3=ZatGizi(titleGizi: "Vitamin B3", definisiGizi: "Pada ibu hamil, Vitamin B3 atau Niasin berguna untuk mengurangi rasa mual serta melancarkan pencernaan ibu hamil. Selain itu, Vitamin B3 pun membantu perkembangan otak, sistem saraf, dan kesehatan kulit si janin.")
 
let VitaminB6=ZatGizi(titleGizi: "Vitamin B6", definisiGizi: "Pada ibu hamil, Vitamin B6 atau Piridoksin berguna untuk perkembangan sistem saraf dan otak janin setiap minggu selama kehamilan. Konsumsi vitamin B6 pun menjaga kadar glukosa serta menghindari berat badan lahir rendah (BBLR) dan cacat saat lahir.")
 
let VitaminB9=ZatGizi(titleGizi: "Vitamin B9", definisiGizi: "Pada ibu hamil, Vitamin B9 (mengandung Asam Folat) sangatlah berguna dan penting. Manfaat mulai dari menunjang pertumbuhan plasenta, mengurangi resiko bayi cacat lahir seperti spina bifida dan bibir sumbing, mengurangi risiko pre-eklampsia, cacat jantung, serta mencegah anemia.")
 
let VitaminB12=ZatGizi(titleGizi: "Vitamin B12", definisiGizi: "Pada ibu hamil, Vitamin B12 atau Cobalamin berguna untuk menjaga kesehatan sistem saraf ibu hamil, serta ketika dikombinasikan dengan asam folat, vitamin ini membantu mencegah spina bifida dan cacat lahir sistem saraf tulang belakang.")
 
//=============================================================================
 
let VitaminC=ZatGizi(titleGizi: "Vitamin C", definisiGizi: "Pada ibu hamil, Vitamin C berguna untuk melindungi dan menjaga kesehatan sel tubuh baik sang Ibu mauapun bayi nya. Selain itu, Vitamin C membantu pembentukan kolagen dalam tulang, otot, kulit, dan pembuluh darah Serta menangkal efek radikal bebas, memperbaiki jaringan tubuh yang rusak, dan mempercepat proses penyembuhan luka.")
 
let VitaminD=ZatGizi(titleGizi: "Vitamin D", definisiGizi: "Pada ibu hamil, Vitamin D berguna untuk perkembangan tulang, gigi, dan fungsi otot bayi agar optimal. Kebutuhan Vitamin D pada ibu hamil biasanya memerlukan bantuan suplementasi dan tidak dapat hanya melalui berjemur dan asupan makanan.")
 
let VitaminE=ZatGizi(titleGizi: "Vitamin E", definisiGizi: "Pada ibu hamil, Vitamin E berguna sebagai antioksidan yang memastikan pembentukan dan perkembangan sel-sel sehat pada janin dan melindungi Ibu hamil dari racun. Vitamin E yang cukup dapat mencegah penuaan plasenta, cedera endotel vaskular, kejadian gangguan hipertensi kehamilan, solusio plasenta, dan kelahiran prematur. Namun, asupan vitamin E yang berlebihan pun dapat mempengaruhi penyerapan dan fungsi vitamin lain yang larut dalam lemak.")
 
let VitaminK=ZatGizi(titleGizi: "Vitamin K", definisiGizi: "Pada ibu hamil, Vitamin K berguna untuk membantu produksi berbagai protein yang dibutuhkan tubuh dalam proses pembekuan darah, dan pembentukan tulang sehingga mencegah pendarahan yang berlebihan, serta menjaga kesehatan jantung dan tulang.")
//=============================================================================
 
let Kalsium=ZatGizi(titleGizi: "Kalsium", definisiGizi: "Pada ibu hamil, Kalsium berguna untuk pembentukan tulang dan gigi. Tubuh Tidak dapat memproduksi kalsium sendiri, sehingga harus dipenuhi dari makanan yang kaya akan kalsium atau melalui suplemen tambahan.")
 
let Fosfor=ZatGizi(titleGizi: "Fosfor", definisiGizi: "Pada ibu hamil, Fosfor berguna untuk mendukung kepadatan tulang dan gigi ibu dan janin dalam kandungan. Selain itu, fosfor juga dibutuhkan untuk kontraksi otot, fungsi ginjal, perbaikan sel dan jaringan tubuh, serta menjaga detak jantung agar tetap normal.")
 
let Magnesium=ZatGizi(titleGizi: "Magnesium", definisiGizi: "Pada ibu hamil, Magnesium berguna untuk menjaga fungsi kekebalan tubuh, pertumbuhan janin dan mencegah kelahiran prematur. Selain itu, mineral ini juga dapat mengurangi stres, menghindari stroke, dan mencegah hipertensi.")
 
let ZatBesi=ZatGizi(titleGizi: "Zat Besi", definisiGizi: "Pada ibu hamil, Zat Besi berguna untuk pembentukan sel darah merah dan hemoglobin yang dibutuhkan untuk memasok oksigen serta nutrisi ke tubuh Ibu dan janin. Kebutuhan zat besi pada ibu hamil meningkat pesat karena adanya peningkatan volume darah ibu yang kisaran umumnya hampir 50%.")
 
let Yodium=ZatGizi(titleGizi: "Yodium", definisiGizi: "Pada ibu hamil, Yodium berguna untuk memproduksi hormon tiroid yang berpengaruh pada perkembangan otak dan organ tubuh janin (saraf), sistem pencernaan, mengendalikan kontraksi otot dan mengatur suhu tubuh.")
 
let Zinc=ZatGizi(titleGizi: "Zinc", definisiGizi: "Pada ibu hamil, Zinc berguna untuk pembentukan DNA pada janin, mempermudah metabolisme tubuh, serta menstabilkan hormon si Ibu sehingga menjaga pertumbuhan otak dan penglihatan janin nantinya.")
 
let Selenium=ZatGizi(titleGizi: "Selenium", definisiGizi: "Pada ibu hamil, Selenium berguna untuk menurunkan resiko ibu terkena pre-eklampsia, serta menyeimbangkan hormon estrogen pada Ibu.")
 
let Mangan=ZatGizi(titleGizi: "Mangan", definisiGizi: "Pada ibu hamil, Mangan berguna untuk membentuk tulang dan jaringan ikat, serta menjamin fungsi saraf yang baik pada janin,. Selain itu, mangan juga penting dalam metabolisme lemak dan karbohidrat, serta penyerapan kalsium dan regulasi gula dalam darah.")

