let JusTomatWortelJeruk=ResepMakanan(titleMakanan: "Jus Tomat Wortel Jeruk", daftarBahanMasakan: "buah tomat ,1 buah wortel ,2 buah jeruk ,secukupnya Air", caraMasakan: "Potong tomat dan wortel ,Peras Jeruk ,Masukkan dalam blender dan tambah sedikit air ,Saring ampas bila tidak menyukai ampas", gambarMasakan: "JusTomatWortelJeruk")
 
let EsJerukLemon=ResepMakanan(titleMakanan: "Es Jeruk Lemon", daftarBahanMasakan: "3 buah jeruk madu ,2 slice lemon ,2 sdt chia seed ,1/2 gelas air matang ,Secukupnya es batu", caraMasakan: "Rendam chia seed dalam air hingga mengembang (30 menitan) ,Peras jeruk dan lemonnya lalu diaduk ,Tuangkan chia seed dan air secukupnya lalu diaduk lagi ,Berikan es batu biar segar dan gula bila tidak terlalu suka asam.", gambarMasakan: "EsJerukLemon")
 
