let SotoSayurAyam=ResepMakanan(titleMakanan: "Soto Sayur Ayam", daftarBahanMasakan: "1/2 ekor ayam potong kecil, 5 tomat hijau, 1 kentang besar, 2 wortel, 3 tangkai daun bawang, 4 tangkai seledri, 3 cabai rawit utuh, 3 daun salam, 3 daun jeruk, 2 serai geprek, 1 ruas jahe geprek, 1 ruas lengkuas geprek secukupnya garam secukupnya gula secukupnya merica Bumbu halus, 3 bawang merah,3 bawang putih, 5 cabai rawit (selera), 6 cabai merah keriting, 2 kemiri,1 ruas kunyit", caraMasakan: "1. Rebus ayam terlebih dahulu sebelum digoreng agar memastikan ayam matang sempurna., 2. Giling bumbu (bawang merah, bawang putih, cabai rawit, cabai merah keriting, kemiri, dan kunyit) sampai halus lalu tumis hingga harum lalu masukkan daun salam, daun jeruk, serai, jahe, dan lengkuas. Beri air lalu masak hingga mendidih., 3. Masukkan sayuran, tunggu beberapa menit lalu tuang ayam. Tidak perlu masak ayam lama-lama karena ayam sudah matang., 4. Beri garam, gula dan merica bubuk lalu koreksi rasa sesuai selera.",gambarMasakan: "SotoSayurAyam")
 
let AyamTeriyaki=ResepMakanan(titleMakanan: "Ayam Teriyaki", daftarBahanMasakan: "250 gram fillet ayam, 1 buah cabe merah ,1 buah cabe hijau ,1 buah bawang bombay ,secukupnya penyedap", caraMasakan: "Cuci ayam, lumuri dengan saori saos teriyaki, tunggu 15 menit, Tumis bawang bombay, cabe hijau dan merah sampai harum, Masukan ayam yg sudah di lumuri, Tambahkan penyedap, Koreksi rasa, tunggu hingga matang ",gambarMasakan: "Ayam Teriyaki")
 
