// Hewan 4
let SusuKurma=ResepMakanan(titleMakanan: "Susu Kurma", daftarBahanMasakan: "25 buah kurma (sudah kupas biji) ,1 Liter susu plain (bisa pakai yang low-fat) ,1-2 sdm madu ,300 ml Air panas", caraMasakan: "Rendam kurma yang sudah dikupas bijinya dengan air panas selama 20 menit. ,Blender kurma dan air rendamannya hingga halus ,Setelah halus, masukkan susu plain dan lanjut blender hingga benar-benar tercampur dan halus. ,Masukkan madu secukupnya ,Lalu blender lagi hingga halus, bila tidak ingin ada serat bisa disaring terlebih dahulu.", gambarMasakan: "SusuKurma")

let BananaChocoMilkSmoothies=ResepMakanan(titleMakanan: "Banana Choco Milk Smoothies", daftarBahanMasakan: "2 buah pisang matang beku ,2-3 sdm susu ibu hamil rasa coklat ,20 ml air mineral dingin ,Topping : ,1 sdm almond panggang ,1 sdt biji chia ,1 sdt quinoa ,1 buah pisang matang", caraMasakan: "Blender pisang, air, dan susu hingga halus ,Tempatkan diwadah dan hiasi dengan topping ,Topping sebenarnya sepengennya Ibu dan susu pun bisa diganti dengan greek yogurt, susu kedelai, susu almond, atau susu low fat.", gambarMasakan: "BananaChocoMilkSmoothies")
 
